    getlooktodistance(re_%1, view.hlookat, view.vlookat, ath, atv);

		if(!ukryj_gora_%1 AND !ukryj_dol_%1 AND !ukryj_lewo_%1 AND !ukryj_prawo_%1 AND re_%1 LT 90,
			set(visible, true);
		,
			set(visible, false);
		);

if(%2 == null,
	vonh(%1);
,
	if(hotspot[%1].%2 == %3,
		vonh(%1);
